import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

interface Marca {
    id?: number;
    nome: string;
}

const MarcaForm: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const navigate = useNavigate();
    const [marca, setMarca] = useState<Marca>({ nome: '' });

    useEffect(() => {
        if (id) {
            fetchMarca(id);
        }
    }, [id]);

    const fetchMarca = async (id: string) => {
        try {
            const response = await axios.get(`http://localhost:8000/api/marcas/${id}`);
            setMarca(response.data);
        } catch (error) {
            console.error('Erro ao buscar marca', error);
        }
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setMarca({ ...marca, [name]: value });
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            if (id) {
                await axios.put(`http://localhost:8000/api/marcas/${id}`, marca);
            } else {
                await axios.post('http://localhost:8000/api/marcas', marca);
            }
            navigate('/marcas');
        } catch (error) {
            console.error('Erro ao salvar marca', error);
        }
    };

    return (
        <div>
            <h2>{id ? 'Editar Marca' : 'Nova Marca'}</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="nome">Nome</label>
                    <input
                        type="text"
                        className="form-control"
                        id="nome"
                        name="nome"
                        value={marca.nome}
                        onChange={handleChange}
                    />
                </div>
                <button type="submit" className="btn btn-primary">
                    {id ? 'Atualizar' : 'Salvar'}
                </button>
            </form>
        </div>
    );
};

export default MarcaForm;
