import React, { useEffect, useState, useContext} from 'react';
import axios from '../axiosConfig';
import { Car } from '../types';
import { useAuth } from '../hooks/useAuth';

interface CarListProps {
    cars: Car[];
    edit: boolean;
  }

const CarList: React.FC<CarListProps> = ({ cars, edit }) => {
    const [carros, setCars] = useState<Car[]>([]);
    const { isAuthenticated, user, logout } = useAuth();
    const token = localStorage.getItem('token');

    useEffect(() => {
        fetchCars();
    }, []);

    const fetchCars = async () => {
        try {
            const response = await axios.get('/carros');
            setCars(response.data.data);
        } catch (error) {
            console.error('Error fetching cars', error);
        }
    };

    const handleDelete = async (id: number) => {
        try {
            await axios.delete(`/carros/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            fetchCars();
        } catch (error) {
            console.error('Error deleting car', error);
        }
    };

    const handleEdit = (id: number) => {
        // Implementar a lógica de edição, talvez redirecionando para um formulário de edição
    };

    return (
        <div className="car-list">
            <h2>Lista de Carros</h2>
            <ul>
                {cars.map((car) => (
                    <li key={car.id}>
                        <div>
                            <p>Marca: {car.marca.nome}</p>
                            <p>Modelo: {car.modelo.nome}</p>
                            <p>Ano: {car.ano}</p>
                            <p>Preço: {car.preco}</p>
                            <p>Quilometragem: {car.quilometragem}</p>
                            {isAuthenticated && edit && (
                                <div>
                                    <button onClick={() => handleEdit(car.id)}>Editar</button>
                                    <button onClick={() => handleDelete(car.id)}>Excluir</button>
                                </div>
                            )}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default CarList;
