import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import CarList from './Components/CarList';
import CarDetail from './Components/CarDetail';
import CarForm from './Components/CarForm';
import Login from './Components/Login';
import Register from './Components/Register';
import Header from './Components/Header';
import Footer from './Components/Footer';
import { AuthProvider } from './contexts/AuthContext';
import MarcaList from './Components/MarcaList';
import ModeloList from './Components/ModeloList';
import MarcaForm from './Components/MarcaForm';
import ModeloForm from './Components/ModeloForm';
import FilterSidebar from './Components/FilterSidebar';
import { Car, Filters } from './types';

const App: React.FC = () => {
  const [cars, setCars] = useState<Car[]>([]);
  const [filters, setFilters] = useState<Filters>({ brand: '', year: '' });

  useEffect(() => {
      fetchCars();
  }, [filters]);

  const fetchCars = async () => {
      try {
          const response = await axios.get('http://localhost:8000/api/carros', { params: filters });
          setCars(response.data);
      } catch (error) {
          console.error('Error fetching cars', error);
      }
  };

  const handleFilterChange = (newFilters: Filters) => {
      setFilters(newFilters);
  };

  return (
      <Router>
          <div className="container-fluid">
              <Header />
              <div className="row">
                  <div className="col-md-3">
                      <FilterSidebar onFilterChange={handleFilterChange} />
                  </div>
                  <div className="col-md-9">
                  <Routes>
                    <Route path="/"  element={<CarList />} />
                    <Route path="/carros/:id" element={<CarDetail />} />
                    <Route path="/novo" element={<CarForm/>} />
                    <Route path="/editar/:id" element={<CarForm />} />
                    <Route path="/login" element={<Login />} />
                    <Route path="/register" element={<Register />} />
                    <Route path="/marcas/nova" element={<MarcaList />} />
                    <Route path="/marcas/:id" element={<MarcaForm />} />
                    <Route path="/modelos" element={<ModeloList />} />
                    <Route path="/modelos/novo" element={<ModeloForm />} />
                    <Route path="/modelos/:id" element={<ModeloForm />} />
                    <Route path="/" element={() => <CarList cars={cars} />} />
                  </Routes>
                  </div>
              </div>
              <Footer />
          </div>
      </Router>
  );
};

export default App;
