import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { RouteComponentProps } from 'react-router-dom';
import { Modelo, Marca } from '../types';

interface MatchParams {
    id: string;
}

interface Props extends RouteComponentProps<MatchParams> {}

const ModeloForm: React.FC<Props> = ({ match, history }) => {
    const [nome, setNome] = useState<string>('');
    const [marcaId, setMarcaId] = useState<number>(0);
    const [marcas, setMarcas] = useState<Marca[]>([]);

    useEffect(() => {
        fetchMarcas();
        if (match.params.id) {
            fetchModelo(match.params.id);
        }
    }, [match.params.id]);

    const fetchMarcas = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/marcas');
            setMarcas(response.data);
        } catch (error) {
            console.error('Error fetching marcas', error);
        }
    };

    const fetchModelo = async (id: string) => {
        try {
            const response = await axios.get(`http://localhost:8000/api/modelos/${id}`);
            setNome(response.data.nome);
            setMarcaId(response.data.marca_id);
        } catch (error) {
            console.error('Error fetching modelo', error);
        }
    };

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();

        const modelo: Modelo = { id: match.params.id ? parseInt(match.params.id) : 0, nome, marca_id: marcaId };

        try {
            if (match.params.id) {
                await axios.put(`http://localhost:8000/api/modelos/${modelo.id}`, modelo);
            } else {
                await axios.post('http://localhost:8000/api/modelos', modelo);
            }
            history.push('/modelos');
        } catch (error) {
            console.error('Error saving modelo', error);
        }
    };

    return (
        <div>
            <h2>{match.params.id ? 'Editar Modelo' : 'Adicionar Modelo'}</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="nome">Nome</label>
                    <input
                        type="text"
                        className="form-control"
                        id="nome"
                        value={nome}
                        onChange={(e) => setNome(e.target.value)}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="marca">Marca</label>
                    <select
                        className="form-control"
                        id="marca"
                        value={marcaId}
                        onChange={(e) => setMarcaId(parseInt(e.target.value))}
                    >
                        <option value="0">Selecione uma marca</option>
                        {marcas.map(marca => (
                            <option key={marca.id} value={marca.id}>{marca.nome}</option>
                        ))}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Salvar</button>
            </form>
        </div>
    );
};

export default ModeloForm;
