import React, { useEffect, useState, useContext} from 'react';
import axios from '../axiosConfig';
import { Car } from '../types';
import { useAuth } from '../hooks/useAuth';
import { Container, Row, Col, Card, Button, Form, Pagination } from 'react-bootstrap';
import { Link } from 'react-router-dom';

interface CarListProps {
    cars: Car[];
    edit: boolean;
  }

const CarList: React.FC<CarListProps> = ({ cars, edit }) => {
    const [carros, setCars] = useState<Car[]>([]);
    const { isAuthenticated, user, logout } = useAuth();
    const token = localStorage.getItem('token');

    useEffect(() => {
        fetchCars();
    }, []);

    const fetchCars = async () => {
        try {
            const response = await axios.get('/carros');
            setCars(response.data.data);
        } catch (error) {
            console.error('Error fetching cars', error);
        }
    };

    const handleDelete = async (id: number) => {
        try {
            await axios.delete(`/carros/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            fetchCars();
        } catch (error) {
            console.error('Error deleting car', error);
        }
    };

    const handleEdit = (id: number) => {
