    };

    return (
        <Container>
            <div className="car-list">
            <h2>Lista de Carros</h2>
            <Row>
                {cars.map((car) => (
                <Col key={car.id} sm={6} md={4} lg={3} className="mb-4">
                    <Card>
                        <Card.Body>
                            <Card.Title>{car.marca.nome} {car.modelo.nome}</Card.Title>
                            <Card.Text>
                            Ano: {car.ano}<br />
                            Preço: R$ {car.preco}<br />
                            Quilometragem: {car.quilometragem} km
                            </Card.Text>
                            <Link to={`/carros/${car.id}`} className="btn btn-info">Detalhes</Link>
                            {isAuthenticated && edit && (
                            <div className="mt-2">
                                <Link to={`/editar/${car.id}`} className="btn btn-warning mr-2">Editar</Link>
                                <Button variant="danger" onClick={() => handleDelete(car.id)}>Excluir</Button>
                            </div>
                            )}
                        </Card.Body>
