import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Filters } from '../types';

interface FilterSidebarProps {
    onFilterChange: (filters: Filters) => void;
}

const FilterSidebar: React.FC<FilterSidebarProps> = ({ onFilterChange }) => {
    const [filters, setFilters] = useState<Filters>({ brand: '', year: '' });
    const [brands, setBrands] = useState<{ id: number; nome: string }[]>([]);
    const [years, setYears] = useState<number[]>([]);

    useEffect(() => {
        fetchBrands();
        fetchYears();
    }, []);

    const fetchBrands = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/marcas');
            setBrands(response.data);
        } catch (error) {
            console.error('Erro ao buscar marcas', error);
        }
    };

    const fetchYears = async () => {
        try {
            const response = await axios.get('/years.json');
            const years = response.data.years.sort((a: number, b: number) => b - a); // Ordenar em ordem decrescente
            setYears(years);
        } catch (error) {
            console.error('Erro ao buscar anos', error);
        }
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value } = e.target;
        setFilters({ ...filters, [name]: value });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        onFilterChange(filters);
    };

    return (
        <div>
            <h2>Filtrar Carros</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="brand">Marca</label>
                    <select
                        className="form-control"
                        id="brand"
                        name="brand"
                        value={filters.brand}
                        onChange={handleChange}
                    >
                        <option value="">Selecione uma marca</option>
                        {brands.map(brand => (
                            <option key={brand.id} value={brand.nome}>
                                {brand.nome}
                            </option>
                        ))}
                    </select>
                </div>
                <div className="form-group">
                    <label htmlFor="year">Ano</label>
                    <select
                        className="form-control"
                        id="year"
                        name="year"
                        value={filters.year}
                        onChange={handleChange}
                    >
                        <option value="">Selecione um ano</option>
                        {years.map(year => (
                            <option key={year} value={year}>
                                {year}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit" className="btn btn-primary">Filtrar</button>
            </form>
        </div>
    );
};

export default FilterSidebar;
